<?php

namespace Paypal\PaypalIntegrationBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * ShopPaypalInformationRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ShopPaypalInformationRepository extends EntityRepository
{
    
    /**
     *  function for calculating the paypal accounts for a shop
     * @param type $shop_id
     * @return type
     */
    public function countPaypals($shop_id) {
        $qb = $this->createQueryBuilder('s');
              $qb->where('s.status =:enabled')		
                ->andWhere('s.isDeleted=:delstatus')
                ->andWhere('s.shopId=:store_id')
		->setParameter('store_id',$shop_id)
		->setParameter('enabled','VERIFIED')
                ->setParameter('delstatus',0);
        $query = $qb->getQuery();
        $result = $query->getResult();
        return count($result);
    }
}
