<?php

namespace StoreManager\StoreBundle\Repository;

use Doctrine\ODM\MongoDB\DocumentRepository;

/**
 * AffiliationMongoRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class AffiliationMongoRepository extends DocumentRepository
{
    
    public function countAffiStore($user_id,$start_date_month,$end_date_month){
        //create the query
        $qb     = $this->createQueryBuilder();
        $result = $qb->field('user_id')->equals($user_id)
                  ->field('affiliation_date')->range($start_date_month, $end_date_month)
                  ->getQuery()
                  ->execute()
		  ->toArray(false);
        return $result;
    }
}