<?php

namespace UserManager\Sonata\UserBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * RelationTypeRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RelationTypeRepository extends EntityRepository
{
    
    /**
    * Insert Relation Type
    * @return object array
    */
    public function InsertRelationType($datas){ 
        
        
        foreach($datas as $data){

            $details = new RelationType();
            
            $details->setName($data['name']);
             
            $em = $this->getEntityManager();

            $em->persist($details);
            
        }
                                 
        $em->flush();
        
        return true;
    }
}
