<?php

namespace UserManager\Sonata\UserBundle\Repository;

use Doctrine\ODM\MongoDB\DocumentRepository;

/**
 * GroupAlbumMongoRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class GroupAlbumMongoRepository extends DocumentRepository
{
     /**
     * Edit the club album rate
     * @param type $rate_id
     * @return boolean
     */
    public function editAlbumRate($rate_id, $arrayAlbumRate, $album_id) {
        $result = $this->createQueryBuilder('post')
                ->update()
                ->field('id')->equals($album_id)
                ->field('rate.id')->equals($rate_id)
                ->field("rate.$")->set($arrayAlbumRate)
                ->getQuery()
                ->execute();
        return true;
    }

}