<?php

namespace Utility\MasterDataBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * familyRelationShipTypeRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class FamilyRelationShipTypeRepository extends EntityRepository
{
   /**
    * FamilyRelation List
    * @return object array
    */
    public function getFamilyRelationList($langCode, $parent_id){ 
        try{
        //object of query builder.
        $qb = $this->createQueryBuilder('c');
        $query = $qb->select('c.id, code.relationshipName as language_string')
                ->innerJoin('MasterDataBundle:FamilyRelationShipTypeCodeByLang', 'code', 'WITH', 'code.relationshipCode = c.name')
                ->where('code.langCode =:langCode','c.parent=:parentId', 'c.status =:status')
                ->setParameter('langCode',$langCode )
                ->setParameter('parentId',$parent_id )
                ->setParameter('status',1)
                ->getQuery();
        $response = $query->getResult();
        return $response;
        }catch(\Exception $e){
            return array();
        }
    }
}
