<?php

namespace Utility\MasterDataBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * PrivacySettingRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PrivacySettingRepository extends EntityRepository
{
   /**
    * Privacy Setting List
    * @return object array
    */
    public function getPrivacySettingList($langCode){ 
        try{
        //object of query builder.
        $qb = $this->createQueryBuilder('c');
        $query = $qb->select('c.id, code.privacySettingName as language_string')
                ->innerJoin('MasterDataBundle:PrivacySettingCodeByLang', 'code', 'WITH', 'code.privacySettingCode = c.name')
                ->where('code.langCode =:langCode','c.status =:status')
                ->setParameter('langCode',$langCode )
                ->setParameter('status',1)
                ->getQuery();
        $response = $query->getResult();
        return $response;
        }catch(\Exception $e){
            return array();
        }
    }
}
