<?php

namespace Utility\MasterDataBundle\Repository;

use Doctrine\ORM\EntityRepository;


/**
 * RegionRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RegionRepository extends EntityRepository
{
   /**
    * Region List
    * @return object array
    */
    public function getRegionList($langCode, $parent_id){ 
        try{
        //object of query builder.
        $qb = $this->createQueryBuilder('c');
        $query = $qb->select('c.id, code.regionName as language_string, c.code')
                ->innerJoin('MasterDataBundle:RegionCodeByLang', 'code', 'WITH', 'code.regionCode = c.name')
                ->where('code.langCode =:langCode','c.parent=:parentId', 'c.status =:status')
                ->setParameter('langCode',$langCode )
                ->setParameter('parentId',$parent_id )
                ->setParameter('status',1)
                ->getQuery();
        $response = $query->getResult();
        return $response;
        }catch(\Exception $e){
            return array();
        }
    }
}
